mapper.insert:
INSERT INTO "BLOG" (
    BOARD_NO, IS_PAID, PRICE, TEMP_FL
) VALUES (
    #{boardNo}, #{isPaid}, #{price}, #{tempFl}
)

mapper.select:
SELECT * FROM ( SELECT ROWNUM AS RNUM, A.* FROM ( SELECT b.BOARD_NO AS boardNo, b.BOARD_TITLE AS boardTitle, DBMS_LOB.SUBSTR(b.BOARD_CONTENT, 600, 1) AS boardContent, b.BOARD_COUNT AS boardCount, TO_CHAR(b.B_CREATE_DATE, 'YYYY.MM.DD') AS bCreateDate, m.MEMBER_NICKNAME AS memberNickname, m.MEMBER_EMAIL AS memberEmail,
(SELECT i.IMG_PATH || i.IMG_RENAME FROM BOARD_IMG i WHERE i.BOARD_NO = b.BOARD_NO AND i.IMG_ORDER = 0) AS thumbnailUrl,
(SELECT LISTAGG(T.TAG_NAME, ',') WITHIN GROUP (ORDER BY BT.TAG_NO) FROM BLOG_TAG BT JOIN TAG T ON BT.TAG_NO = T.TAG_NO WHERE BT.BOARD_NO = b.BOARD_NO) AS tagsStr,
bl.IS_PAID AS isPaid, bl.PRICE AS price, (SELECT COUNT(*) FROM "COMMENT" c WHERE c.BOARD_NO = b.BOARD_NO AND c.COMMENT_DEL_FL = 'N') AS commentCount, (SELECT COUNT(*) FROM "BOARD_LIKE" l WHERE l.BOARD_NO = b.BOARD_NO) AS likeCount FROM "BOARD" b JOIN "MEMBER" m ON b.MEMBER_NO = m.MEMBER_NO JOIN "BLOG" bl ON b.BOARD_NO = bl.BOARD_NO WHERE b.BOARD_CODE = 1 AND b.BOARD_DEL_FL = 'N' AND bl.TEMP_FL = 'N'
ORDER BY b.BOARD_COUNT DESC, b.BOARD_NO DESC ORDER BY likeCount DESC, b.BOARD_NO DESC ORDER BY commentCount DESC, b.BOARD_NO DESC ORDER BY b.BOARD_NO DESC
) A ) WHERE RNUM BETWEEN #{offset} + 1 AND #{offset} + #{limit}

mapper.update:
UPDATE "BOARD"
SET BOARD_TITLE = #{boardTitle}, BOARD_CONTENT = #{boardContent}
WHERE BOARD_NO = #{boardNo}

UPDATE "BLOG"
SET IS_PAID = #{isPaid}, PRICE = #{price}, TEMP_FL = #{tempFl}
WHERE BOARD_NO = #{boardNo}

mapper.insert:
INSERT INTO "BOARD_IMG" (
    IMG_NO, IMG_PATH, IMG_RENAME, IMG_ORDER, BOARD_NO
) VALUES (
    SEQ_IMAGE_NO.NEXTVAL, #{imgPath}, #{imgRename}, #{imgOrder}, #{boardNo}
)

mapper.select:
SELECT COUNT(*) 
FROM "SUBSCRIBE" 
WHERE CREATOR_ID = #{memberNo}

SELECT COUNT(*) 
FROM "SUBSCRIBE" 
WHERE SUBSCRIBER_ID = #{me} 
  AND CREATOR_ID = #{target}
