mapper.insert:
SELECT SEQ_COMMENT_NO.NEXTVAL FROM DUAL
INSERT INTO "COMMENT" ( COMMENT_NO, COMMENT_CONTENT, C_CREATE_DATE, COMMENT_DEL_FL, SECRET_YN, MODIFY_YN, BOARD_NO, MEMBER_NO, PARENTS_COMMENT_NO ) VALUES ( #{commentNo}, #{commentContent}, SYSDATE, 'N', 'N', 'N', #{boardNo}, #{memberNo},
NULL #{parentCommentNo}
)

mapper.select:
SELECT MEMBER_NICKNAME
FROM MEMBER
WHERE MEMBER_NO = ${memberNo}
